mapper.insert:
INSERT INTO yjy.tbl_board(
            title            /* 글제목 */
        	,content         /* 글내용 */
        	,writer
            )
     VALUES (
             #{title}
	        ,#{content}
	        ,#{writer}
	 	    )

mapper.update:
UPDATE  yjy.tbl_board
   SET  title   = #{title}
       ,content = #{content}
       ,writer  = #{writer}
 WHERE  bno     = #{bno}
   and  writer  = #{writer}
